<?phpnamespace Nemundo\Db\Provider\MySql\User;use Nemundo\Db\Base\AbstractDbDataSource;use Nemundo\Db\Reader\SqlReader;class MySqlUserReader extends AbstractDbDataSource{    /**     * @var string     */    public $filter;    /**     * @return MySqlUserItem[]     */    public function getData()    {        return parent::getData();    }    protected function loadData()    {        $this->checkConnection();        $reader = new SqlReader();        $reader->connection = $this->connection;        $reader->sqlStatement->sql = 'SELECT * FROM mysql.user;';        foreach ($reader->getData() as $row) {            $item = new MySqlUserItem();            $item->user = $row->getValue('User');            $item->host = $row->getValue('Host');            $this->addItem($item);        }    }}