<?phpnamespace Nemundo\Db\Sql\Field\Geo;use Nemundo\Core\Type\Geo\GeoCoordinate;use Nemundo\Core\Type\Geo\GeoCoordinateAltitude;use Nemundo\Db\Sql\Field\AbstractField;use Nemundo\Db\Sql\Field\Field;class DistanceField extends AbstractField{    /**     * @var AbstractField     */    public $geoCoordinateLatitudeField;    /**     * @var AbstractField     */    public $geoCoordinateLongitudeField;    /**     * @var GeoCoordinate|GeoCoordinateAltitude     */    public $geoCoordinate;    /**     * @var string     */    //protected $prefix;    public function getFieldName()    {        $sql = 'ST_DISTANCE_SPHERE(POINT(' . $this->geoCoordinate->latitude . ',' . $this->geoCoordinate->longitude . '),POINT(' . $this->geoCoordinateLatitudeField->getConditionFieldName() . ',' . $this->geoCoordinateLongitudeField->getConditionFieldName() . ') ';        $sql .= $this->getConditionFieldName();        return $sql;    }    public function getConditionFieldName()    {        $fieldName = 'distance';  // $this->prefix . '_' . $this->aggregateField->fieldName;        $this->aliasFieldName = $fieldName;        return $fieldName;    }}