<?phpnamespace Nemundo\Db\Sql\Field\Geo;use Nemundo\Core\Type\Geo\GeoCoordinate;use Nemundo\Core\Type\Geo\GeoCoordinateAltitude;use Nemundo\Db\Sql\Field\AbstractField;class DistanceField extends AbstractField{    /**     * @var AbstractField     */    public $geoCoordinateLatitudeField;    /**     * @var AbstractField     */    public $geoCoordinateLongitudeField;    /**     * @var GeoCoordinate|GeoCoordinateAltitude     */    public $geoCoordinate;    public $distanceFieldName = 'geo_distance';    public function __construct()    {        parent::__construct();        $this->geoCoordinate = new GeoCoordinate();    }    public function getFieldName()    {        $sql = 'ST_DISTANCE_SPHERE(POINT(' . $this->geoCoordinate->longitude . ',' . $this->geoCoordinate->latitude . '),POINT(' . $this->geoCoordinateLongitudeField->getConditionFieldName() . ',' . $this->geoCoordinateLatitudeField->getConditionFieldName() . ')) ';        $sql .= $this->getConditionFieldName();        return $sql;    }    public function getConditionFieldName()    {        $fieldName = $this->distanceFieldName;        $this->aliasFieldName = $fieldName;        return $fieldName;    }}